INSERT INTO `config_info` VALUES (1, 'user-provider-dev.yaml', 'DEV_GROUP', 'spring:\r\n  main:\r\n    allow-bean-definition-overriding: true #当遇到同样名字的时候 允许覆盖注册\r\n  datasource:\r\n    url: jdbc:mysql://localhost:3306/zptc?characterEncoding=utf-8&useSSL=false&serverTimezone=Asia/Shanghai&rewriteBatchedStatements=true\r\n    username: root\r\n    password: 123456\r\n    type: com.alibaba.druid.pool.DruidDataSource\r\n    druid:\r\n      #初始化大小，最小，最大\r\n      initial-size: 5\r\n      min-idle: 5\r\n      max-active: 20\r\n      max-wait: 60000 #配置获取连接等待超时的时间\r\n      time-between-eviction-runs-millis: 60000 #配置间隔多久才进行一次检测，检测需要关闭的空闲连接，单位是毫秒\r\n      min-evictable-idle-time-millis: 300000 #配置一个连接在池中最小生存的时间，单位是毫秒\r\n      validation-query: SELECT 1 FROM DUAL\r\n      test-while-idle: true\r\n      test-on-borrow: false\r\n      test-on-return: false\r\n      #打开PSCache，并且指定每个连接上PSCache的大小\r\n      pool-prepared-statements: true\r\n      #配置监控统计拦截的filters，去掉后监控界面sql无法统计，\'wall\'用于防火墙\r\n      max-pool-prepared-statement-per-connection-size: 20\r\n      filters: stat,wall,slf4j\r\n      use-global-data-source-stat: true\r\n      connect-properties: druid.stat.mergeSql=true;druid.stat.slowSqlMillis=5000 #通过connectProperties属性来打开mergeSql功能；慢SQL记录\r\n  redis:\r\n    host: 127.0.0.1\r\n    port: 6379\r\n  cloud:\r\n    sentinel:\r\n      transport:\r\n        dashboard: localhost:8080 #配置Sentinel dashboard地址\r\n        port: 8719 #默认是8719 假如被占用会自动从8719开始一次+1扫描 直至找到未被占用的端口\r\n    alibaba:\r\n      seata:\r\n        #自定义事务组名称需要与seata-server中的对应\r\n        tx-service-group: zptc_tx_group\r\n\r\nmybatis:\r\n  #开启驼峰命名匹配规则\r\n  configuration:\r\n    map-underscore-to-camel-case: true\r\n  mapper-locations: classpath:mapper/*.xml\r\n  type-aliases-package: cn.zptc.ai.entity\r\n\r\nmanagement:\r\n  endpoints:\r\n    web:\r\n      exposure:\r\n        include: \"*\" #暴露需要监控的内容\r\n\r\n#激活Sentinel对Feign的支持\r\nfeign:\r\n  sentinel:\r\n    enabled: true', '220a5fa40398f8897b8fb89ca2c54d40', '2020-06-21 20:20:00', '2020-06-21 20:20:00', NULL, '0:0:0:0:0:0:0:1', '', 'f331a483-3c21-4b77-88b0-d810102a5368', NULL, NULL, NULL, 'yaml', NULL);
